package encoding

import (
	"encoding/hex"
	"testing"

	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/ethereum/go-ethereum/crypto"
	"gopkg.in/go-playground/assert.v1"
)

func Test_EncodeABISignalProof(t *testing.T) {
	// nolint: lll
	accProof1, _ := hex.DecodeString("")

	// nolint: lll
	accProof2, _ := hex.DecodeString("")

	// nolint: lll
	sProof1, _ := hex.DecodeString("")

	// nolint: lll
	sProof2, _ := hex.DecodeString("")

	s := []HopProof{
		{
			ChainID:      1,
			BlockID:      1,
			RootHash:     crypto.Keccak256Hash([]byte("ROOT_HASH")),
			CacheOption:  0,
			AccountProof: [][]byte{accProof1, accProof2},
			StorageProof: [][]byte{sProof1, sProof2},
		},
	}

	// nolint: lll
	want := ""
	proof, err := EncodeHopProofs(s)
	assert.Equal(t, nil, err)
	assert.Equal(t, hexutil.Encode(proof), want)
}
